package com.yihu.myt.dao;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.ConsEnrolSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.ConsEnrolVo;
public class ConsEnrolDAO {
	/**	*添加	*/	public void insertConsEnrol(ConsEnrolVo vo,JdbcConnection conn) throws Exception{		Sql sql = DB.me().createSql(ConsEnrolSqlNameEnum.insertConsEnrol);		sql.addParamValue(vo.getCONSENROLID());		sql.addParamValue(vo.getDOCTORID());		sql.addParamValue(vo.getSEVENDOCTORID());		sql.addParamValue(vo.getTENDOCTORID());		sql.addParamValue(vo.getDOCTORNAME());		sql.addParamValue(vo.getOPERCONFID());		sql.addParamValue(vo.getHOSPOFFICEID());		sql.addParamValue(vo.getHOSPOFFICENAME());		sql.addParamValue(vo.getCARDID());		sql.addParamValue(vo.getCUSTNAME());		sql.addParamValue(vo.getCUSTPHONE());		sql.addParamValue(vo.getCONSTYPE());		sql.addParamValue(vo.getISAPPOINT());		sql.addParamValue(vo.getVOUCHRESULT());		sql.addParamValue(vo.getVOUCHDEFEAT());		sql.addParamValue(vo.getTURNRESULT());		sql.addParamValue(vo.getTURNDEFEAT());		sql.addParamValue(vo.getISREVERT());		sql.addParamValue(vo.getNOREVERT());		sql.addParamValue(vo.getORGID());		sql.addParamValue(vo.getCITYID());		sql.addParamValue(vo.getOPERATORID());		sql.addParamValue(vo.getOPERATORNAME());		sql.addParamValue(vo.getOPERTIME());		sql.addParamValue(vo.getSTATE());		sql.addParamValue(vo.getREMARK());		sql.addParamValue(vo.getCardtypesn());		sql.addParamValue(vo.getCardorgid());		sql.addParamValue(vo.getProvinceId());		sql.addParamValue(vo.getPackageId());		DB.me().insert(conn,sql);		}
	/**	*修改	*/	public void updateConsEnrolByCondition(ConsEnrolVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(ConsEnrolSqlNameEnum.updateConsEnrolByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getCONSENROLID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getCONSENROLID());		}		DB.me().update(conn, sql);		}
}