package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.ArticleSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.ArticleVo;import com.yihu.myt.service.service.IArticleService;
public class ArticleService implements IArticleService{
	/**	*获取列表记录数	*/	public Integer queryArticleCountByCondition(ArticleVo vo) throws Exception{		Sql sql = DB.me().createSql(ArticleSqlNameEnum.queryArticleCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<ArticleVo> queryArticleListByCondition(ArticleVo vo) throws Exception{		Sql sql = DB.me().createSql(ArticleSqlNameEnum.queryArticleListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<ArticleVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,ArticleVo.class);		return list;	}
	/**	*添加	*/	public void insertArticle(ArticleVo vo) throws Exception{}
	/**	*修改	*/	public void updateArticleByCondition(ArticleVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(ArticleSqlNameEnum.updateArticleByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getARTICLE_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getARTICLE_ID());		}		DB.me().update(conn, sql);		}
}