package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.AskSmsRecordSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.AskSmsRecordVo;import com.yihu.myt.service.service.IAskSmsRecordService;
public class AskSmsRecordService implements IAskSmsRecordService{
	/**	*获取列表记录数	*/	public Integer queryAskSmsRecordCountByCondition(AskSmsRecordVo vo) throws Exception{		Sql sql = DB.me().createSql(AskSmsRecordSqlNameEnum.queryAskSmsRecordCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<AskSmsRecordVo> queryAskSmsRecordListByCondition(AskSmsRecordVo vo) throws Exception{		Sql sql = DB.me().createSql(AskSmsRecordSqlNameEnum.queryAskSmsRecordListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<AskSmsRecordVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,AskSmsRecordVo.class);		return list;	}
	/**	*添加	*/	public void insertAskSmsRecord(AskSmsRecordVo vo) throws Exception{}
	/**	*修改	*/	public void updateAskSmsRecordByCondition(AskSmsRecordVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(AskSmsRecordSqlNameEnum.updateAskSmsRecordByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getAsr_id())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getAsr_id());		}		DB.me().update(conn, sql);		}
}