package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.AssayPropertySqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.AssayPropertyVo;import com.yihu.myt.service.service.IAssayPropertyService;
public class AssayPropertyService implements IAssayPropertyService{
	/**	*获取列表记录数	*/	public Integer queryAssayPropertyCountByCondition(AssayPropertyVo vo) throws Exception{		Sql sql = DB.me().createSql(AssayPropertySqlNameEnum.queryAssayPropertyCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<AssayPropertyVo> queryAssayPropertyListByCondition(AssayPropertyVo vo) throws Exception{		Sql sql = DB.me().createSql(AssayPropertySqlNameEnum.queryAssayPropertyListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<AssayPropertyVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,AssayPropertyVo.class);		return list;	}
	/**	*添加	*/	public void insertAssayProperty(AssayPropertyVo vo) throws Exception{}
	/**	*修改	*/	public void updateAssayPropertyByCondition(AssayPropertyVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(AssayPropertySqlNameEnum.updateAssayPropertyByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getAP_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getAP_ID());		}		DB.me().update(conn, sql);		}
}