package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.AssayResultListSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.AssayResultListVo;import com.yihu.myt.service.service.IAssayResultListService;
public class AssayResultListService implements IAssayResultListService{
	/**	*获取列表记录数	*/	public Integer queryAssayResultListCountByCondition(AssayResultListVo vo) throws Exception{		Sql sql = DB.me().createSql(AssayResultListSqlNameEnum.queryAssayResultListCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<AssayResultListVo> queryAssayResultListListByCondition(AssayResultListVo vo) throws Exception{		Sql sql = DB.me().createSql(AssayResultListSqlNameEnum.queryAssayResultListListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<AssayResultListVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,AssayResultListVo.class);		return list;	}
	/**	*添加	*/	public void insertAssayResultList(AssayResultListVo vo) throws Exception{}
	/**	*修改	*/	public void updateAssayResultListByCondition(AssayResultListVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(AssayResultListSqlNameEnum.updateAssayResultListByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getARL_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getARL_ID());		}		DB.me().update(conn, sql);		}
}