package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.BulletinSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.BulletinVo;import com.yihu.myt.service.service.IBulletinService;
public class BulletinService implements IBulletinService{
	/**	*获取列表记录数	*/	public Integer queryBulletinCountByCondition(BulletinVo vo) throws Exception{		Sql sql = DB.me().createSql(BulletinSqlNameEnum.queryBulletinCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<BulletinVo> queryBulletinListByCondition(BulletinVo vo) throws Exception{		Sql sql = DB.me().createSql(BulletinSqlNameEnum.queryBulletinListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<BulletinVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,BulletinVo.class);		return list;	}
	/**	*添加	*/	public void insertBulletin(BulletinVo vo) throws Exception{}
	/**	*修改	*/	public void updateBulletinByCondition(BulletinVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(BulletinSqlNameEnum.updateBulletinByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getBULL_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getBULL_ID());		}		DB.me().update(conn, sql);		}
}