package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.DictionarySqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.DictionaryVo;import com.yihu.myt.service.service.IDictionaryService;
public class DictionaryService implements IDictionaryService{
	/**	*获取列表记录数	*/	public Integer queryDictionaryCountByCondition(DictionaryVo vo) throws Exception{		Sql sql = DB.me().createSql(DictionarySqlNameEnum.queryDictionaryCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<DictionaryVo> queryDictionaryListByCondition(DictionaryVo vo) throws Exception{		Sql sql = DB.me().createSql(DictionarySqlNameEnum.queryDictionaryListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<DictionaryVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,DictionaryVo.class);		return list;	}
	/**	*添加	*/	public void insertDictionary(DictionaryVo vo) throws Exception{}
	/**	*修改	*/	public void updateDictionaryByCondition(DictionaryVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(DictionarySqlNameEnum.updateDictionaryByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getDICT_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getDICT_ID());		}		DB.me().update(conn, sql);		}
}