package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.DiseaseClickSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.DiseaseClickVo;import com.yihu.myt.service.service.IDiseaseClickService;
public class DiseaseClickService implements IDiseaseClickService{
	/**	*获取列表记录数	*/	public Integer queryDiseaseClickCountByCondition(DiseaseClickVo vo) throws Exception{		Sql sql = DB.me().createSql(DiseaseClickSqlNameEnum.queryDiseaseClickCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<DiseaseClickVo> queryDiseaseClickListByCondition(DiseaseClickVo vo) throws Exception{		Sql sql = DB.me().createSql(DiseaseClickSqlNameEnum.queryDiseaseClickListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<DiseaseClickVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,DiseaseClickVo.class);		return list;	}
	/**	*添加	*/	public void insertDiseaseClick(DiseaseClickVo vo) throws Exception{}
	/**	*修改	*/	public void updateDiseaseClickByCondition(DiseaseClickVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(DiseaseClickSqlNameEnum.updateDiseaseClickByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getDIC_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getDIC_ID());		}		DB.me().update(conn, sql);		}
}