package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.DoctorAccountSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.DoctorAccountVo;import com.yihu.myt.service.service.IDoctorAccountService;
public class DoctorAccountService implements IDoctorAccountService{
	/**	*获取列表记录数	*/	public Integer queryDoctorAccountCountByCondition(DoctorAccountVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorAccountSqlNameEnum.queryDoctorAccountCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<DoctorAccountVo> queryDoctorAccountListByCondition(DoctorAccountVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorAccountSqlNameEnum.queryDoctorAccountListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<DoctorAccountVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,DoctorAccountVo.class);		return list;	}
	/**	*添加	*/	public void insertDoctorAccount(DoctorAccountVo vo) throws Exception{}
	/**	*修改	*/	public void updateDoctorAccountByCondition(DoctorAccountVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(DoctorAccountSqlNameEnum.updateDoctorAccountByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getDA_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getDA_ID());		}		DB.me().update(conn, sql);		}
}