package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.DoctorAccount_MoreSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.DoctorAccount_MoreVo;import com.yihu.myt.service.service.IDoctorAccount_MoreService;
public class DoctorAccount_MoreService implements IDoctorAccount_MoreService{
	/**	*获取列表记录数	*/	public Integer queryDoctorAccount_MoreCountByCondition(DoctorAccount_MoreVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorAccount_MoreSqlNameEnum.queryDoctorAccount_MoreCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<DoctorAccount_MoreVo> queryDoctorAccount_MoreListByCondition(DoctorAccount_MoreVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorAccount_MoreSqlNameEnum.queryDoctorAccount_MoreListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<DoctorAccount_MoreVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,DoctorAccount_MoreVo.class);		return list;	}
	/**	*添加	*/	public void insertDoctorAccount_More(DoctorAccount_MoreVo vo) throws Exception{}
	/**	*修改	*/	public void updateDoctorAccount_MoreByCondition(DoctorAccount_MoreVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(DoctorAccount_MoreSqlNameEnum.updateDoctorAccount_MoreByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getDa_id())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getDa_id());		}		DB.me().update(conn, sql);		}
}