package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.DoctorClickWaterSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.DoctorClickWaterVo;import com.yihu.myt.service.service.IDoctorClickWaterService;
public class DoctorClickWaterService implements IDoctorClickWaterService{
	/**	*获取列表记录数	*/	public Integer queryDoctorClickWaterCountByCondition(DoctorClickWaterVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorClickWaterSqlNameEnum.queryDoctorClickWaterCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<DoctorClickWaterVo> queryDoctorClickWaterListByCondition(DoctorClickWaterVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorClickWaterSqlNameEnum.queryDoctorClickWaterListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<DoctorClickWaterVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,DoctorClickWaterVo.class);		return list;	}
	/**	*添加	*/	public void insertDoctorClickWater(DoctorClickWaterVo vo) throws Exception{}
	/**	*修改	*/	public void updateDoctorClickWaterByCondition(DoctorClickWaterVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(DoctorClickWaterSqlNameEnum.updateDoctorClickWaterByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getWaterID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getWaterID());		}		DB.me().update(conn, sql);		}
}