package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.DoctorInviteSMSSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.DoctorInviteSMSVo;import com.yihu.myt.service.service.IDoctorInviteSMSService;
public class DoctorInviteSMSService implements IDoctorInviteSMSService{
	/**	*获取列表记录数	*/	public Integer queryDoctorInviteSMSCountByCondition(DoctorInviteSMSVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorInviteSMSSqlNameEnum.queryDoctorInviteSMSCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<DoctorInviteSMSVo> queryDoctorInviteSMSListByCondition(DoctorInviteSMSVo vo) throws Exception{		Sql sql = DB.me().createSql(DoctorInviteSMSSqlNameEnum.queryDoctorInviteSMSListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<DoctorInviteSMSVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,DoctorInviteSMSVo.class);		return list;	}
	/**	*添加	*/	public void insertDoctorInviteSMS(DoctorInviteSMSVo vo) throws Exception{}
	/**	*修改	*/	public void updateDoctorInviteSMSByCondition(DoctorInviteSMSVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(DoctorInviteSMSSqlNameEnum.updateDoctorInviteSMSByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getID());		}		DB.me().update(conn, sql);		}
}