package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.JobpaySqlNameEnum;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.service.service.IJobpayService;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.JobpayVo;
public class JobpayService implements IJobpayService{
	/**	*获取列表记录数	*/	public Integer queryJobpayCountByCondition(JobpayVo vo) throws Exception{		Sql sql = DB.me().createSql(JobpaySqlNameEnum.queryJobpayCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<JobpayVo> queryJobpayListByCondition(JobpayVo vo) throws Exception{		Sql sql = DB.me().createSql(JobpaySqlNameEnum.queryJobpayListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<JobpayVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,JobpayVo.class);		return list;	}
	/**	*添加	*/	public void insertJobpay(JobpayVo vo) throws Exception{		Sql sql = DB.me().createSql(JobpaySqlNameEnum.insertJobpay);		sql.addParamValue(vo.getDOCID());		sql.addParamValue(vo.getNUM());		sql.addParamValue(vo.getSEQNUM());		sql.addParamValue(vo.getROUTEMESSAGE());		sql.addParamValue(vo.getFLAG());		DB.me().insert(MyDatabaseEnum.YiHuNet2008,sql);		}
	/**	*修改	*/	public void updateJobpayByCondition(JobpayVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(JobpaySqlNameEnum.updateJobpayByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getID());		}		DB.me().update(conn, sql);		}
}