package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.LoginLogSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.LoginLogVo;import com.yihu.myt.service.service.ILoginLogService;
public class LoginLogService implements ILoginLogService{
	/**	*获取列表记录数	*/	public Integer queryLoginLogCountByCondition(LoginLogVo vo) throws Exception{		Sql sql = DB.me().createSql(LoginLogSqlNameEnum.queryLoginLogCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<LoginLogVo> queryLoginLogListByCondition(LoginLogVo vo) throws Exception{		Sql sql = DB.me().createSql(LoginLogSqlNameEnum.queryLoginLogListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<LoginLogVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,LoginLogVo.class);		return list;	}
	/**	*添加	*/	public void insertLoginLog(LoginLogVo vo) throws Exception{}
	/**	*修改	*/	public void updateLoginLogByCondition(LoginLogVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(LoginLogSqlNameEnum.updateLoginLogByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getLOGINLOG_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getLOGINLOG_ID());		}		DB.me().update(conn, sql);		}
}