package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.RecordSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.RecordVo;import com.yihu.myt.service.service.IRecordService;
public class RecordService implements IRecordService{
	/**	*获取列表记录数	*/	public Integer queryRecordCountByCondition(RecordVo vo) throws Exception{		Sql sql = DB.me().createSql(RecordSqlNameEnum.queryRecordCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<RecordVo> queryRecordListByCondition(RecordVo vo) throws Exception{		Sql sql = DB.me().createSql(RecordSqlNameEnum.queryRecordListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<RecordVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,RecordVo.class);		return list;	}
	/**	*添加	*/	public void insertRecord(RecordVo vo) throws Exception{}
	/**	*修改	*/	public void updateRecordByCondition(RecordVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(RecordSqlNameEnum.updateRecordByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getRECORD_Id())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getRECORD_Id());		}		DB.me().update(conn, sql);		}
}