package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.ReportSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.ReportVo;import com.yihu.myt.service.service.IReportService;
public class ReportService implements IReportService{
	/**	*获取列表记录数	*/	public Integer queryReportCountByCondition(ReportVo vo) throws Exception{		Sql sql = DB.me().createSql(ReportSqlNameEnum.queryReportCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<ReportVo> queryReportListByCondition(ReportVo vo) throws Exception{		Sql sql = DB.me().createSql(ReportSqlNameEnum.queryReportListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<ReportVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,ReportVo.class);		return list;	}
	/**	*添加	*/	public void insertReport(ReportVo vo) throws Exception{}
	/**	*修改	*/	public void updateReportByCondition(ReportVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(ReportSqlNameEnum.updateReportByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getREPORT_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getREPORT_ID());		}		DB.me().update(conn, sql);		}
}