package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.WebLettersDetailSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.WebLettersDetailVo;import com.yihu.myt.service.service.IWebLettersDetailService;
public class WebLettersDetailService implements IWebLettersDetailService{
	/**	*获取列表记录数	*/	public Integer queryWebLettersDetailCountByCondition(WebLettersDetailVo vo) throws Exception{		Sql sql = DB.me().createSql(WebLettersDetailSqlNameEnum.queryWebLettersDetailCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<WebLettersDetailVo> queryWebLettersDetailListByCondition(WebLettersDetailVo vo) throws Exception{		Sql sql = DB.me().createSql(WebLettersDetailSqlNameEnum.queryWebLettersDetailListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<WebLettersDetailVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,WebLettersDetailVo.class);		return list;	}
	/**	*添加	*/	public void insertWebLettersDetail(WebLettersDetailVo vo) throws Exception{}
	/**	*修改	*/	public void updateWebLettersDetailByCondition(WebLettersDetailVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(WebLettersDetailSqlNameEnum.updateWebLettersDetailByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getWLD_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getWLD_ID());		}		DB.me().update(conn, sql);		}
}