package com.yihu.myt.service.service.impl;import java.util.List;import com.coreframework.db.DB;import com.coreframework.db.JdbcConnection;import com.coreframework.db.Sql;import com.yihu.myt.enums.MyDatabaseEnum;import com.yihu.myt.enums.WebLettersSqlNameEnum;import com.yihu.myt.util.StringUtil;import com.yihu.myt.vo.WebLettersVo;import com.yihu.myt.service.service.IWebLettersService;
public class WebLettersService implements IWebLettersService{
	/**	*获取列表记录数	*/	public Integer queryWebLettersCountByCondition(WebLettersVo vo) throws Exception{		Sql sql = DB.me().createSql(WebLettersSqlNameEnum.queryWebLettersCountByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		Integer count = DB.me().queryForInteger(MyDatabaseEnum.YiHuNet2008, sql);		return count;	}
	/**	*获取列表	*/	public List<WebLettersVo> queryWebLettersListByCondition(WebLettersVo vo) throws Exception{		Sql sql = DB.me().createSql(WebLettersSqlNameEnum.queryWebLettersListByCondition);		StringBuilder condition = new StringBuilder();		sql.addVar("@condition", condition.toString());		sql.addVar("@page", "");		List<WebLettersVo> list = DB.me().queryForBeanList(MyDatabaseEnum.YiHuNet2008, sql,WebLettersVo.class);		return list;	}
	/**	*添加	*/	public void insertWebLetters(WebLettersVo vo) throws Exception{}
	/**	*修改	*/	public void updateWebLettersByCondition(WebLettersVo vo,JdbcConnection conn) throws Exception{Sql sql = DB.me().createSql(WebLettersSqlNameEnum.updateWebLettersByCondition);		if (vo == null  || StringUtil.isEmpty(vo.getWL_ID())) {			throw new Exception(" 不能为空或者 主键id 不能为空");		}		StringBuilder condition = new StringBuilder();		if (condition.length() == 0) {		throw new Exception("未有更新SQL被执行！");		} else {		condition.deleteCharAt(condition.length() - 1);		sql.addVar("@condition", condition.toString());		sql.addParamValue(vo.getWL_ID());		}		DB.me().update(conn, sql);		}
}